tinymce.PluginManager.add("innerlink", function(editor, url) {
	function showDialog(){
		editor.focus();
			
		var pluginWindow = editor.windowManager.open({
			title : "リンクを挿入",
			width : 320,
			height : 240,
			url : InsertLinkAddress
		});

		editor.windowManager.setParams({
			resize : function(w, h){
				pluginWindow.resizeTo(w, h);

				//pluginWindow.width(w);
				//pluginWindow.height(h);
			},
			width : 320,
			height : 240,
			plugin_url : url,
		});
	}
			
	editor.addButton("innerlink", {
		icon : "link",
		tooltip : "リンクを挿入",
		onclick : showDialog
//		image : url + '/img/icon.gif'
	});

	editor.addMenuItem("innerlink", {
		text : "リンクを挿入",
		onclick : showDialog,
		context : "挿入"
	});
});
