tinymce.PluginManager.add("insertimageshop", function(editor, url) {
	function showDialog(){
		editor.focus();
		
		var pluginWindow = editor.windowManager.open({
			title : "イメージを挿入",
			width : 640,
			height : 400,
			url : InsertImagePage
		});

		editor.windowManager.setParams({
			resize : function(w, h){
				pluginWindow.resizeTo(w, h);

				//pluginWindow.width(w);
				//pluginWindow.height(h);
			},
			width : 640,
			height : 400,
			plugin_url : url,
		});
	}
			
	editor.addButton("insertimageshop", {
		icon : "image",
		tooltip : "イメージを挿入",
		onclick : showDialog
//		image : url + '/img/icon.gif'
	});

	editor.addMenuItem("insertimageshop", {
		text : "イメージを挿入",
		onclick : showDialog,
		context : "挿入"
	});
});